import React,{useState,useEffect} from 'react'
import axios from 'axios';
import './index.css'
import { nanoid } from 'nanoid';
import PubSub from 'pubsub-js';

export default function WheelList() {
    //初始化为一个空数组很重要,没用的话被报undefined错误
    const [data, setdata] = useState([]);
    // {prize_index:0,msg:''}
    const [prize,setprize] = useState();
    const [rotate,setrotate] = useState();
    // 旧写法
    // useEffect(() => {
    //     axios.post('https://qc1hz1.fn.thelarkcloud.com/prizeinfo')
    //     .then(function (res) {
    //         let prize_info = res.data.response;
    //         const n=prize_info.length;
    //         if(n<6){
    //             for(let i=n;i<6;i++){
    //                 prize_info.push({prize_name: "谢谢参与", isprize: false,prize_id:nanoid()});
    //             }   
    //         }
    //         if(n>6){
    //             while(prize_info.length>6){
    //                 prize_info.pop();
    //             }
    //         }
    //         setdata(prize_info);
    //     });
    // }, [])

    // 熟悉新写法!
    useEffect(() => {
        const fetchData=async ()=>{
            const result = await axios('https://qc1hz1.fn.thelarkcloud.com/prizeinfo');
            const prize_info = result.data.response;
            const n=prize_info.length;
            if(n<6){
                for(let i=n;i<6;i++){
                    prize_info.push({prize_name: "谢谢参与", isprize: false,prize_id:nanoid()});
                }   
            }
            if(n>6){
                while(prize_info.length>6){
                    prize_info.pop();
                }
            }
            setdata(prize_info);
        }
        fetchData();
    }, [])

    useEffect(() => {
        const token = PubSub.subscribe('Prize',(msg,item)=>{
            setprize(item);
            const angle=-item.prize_index*60+3600;
            setrotate({angle:angle,transition:'all 5s ease-out'});
        })
        return () => {
            PubSub.unsubscribe(token);
        }
    }, [prize,rotate])

    return (
        // 中间奖品
        <ul id="wheel" className="wheel-list" style={{
            transition:rotate.transition,
            transform:rotate(rotate.angle+'deg')
        }} >
            {
                data.map((prize,index)=>(
                    <li key={prize.prize_id} >
                        <i></i>
                        <div className="prize">
                            <p>{prize.prize_name}</p>
                            <img src={`/images/prize_img${index+1}.png`} alt="" />
                        </div>
                    </li>
                ))
            }
        </ul>
    )
}
