import React, { useEffect } from 'react'
import './index.css'

export default function Wheel_light() {
    const [lightcolors,setlight] = React.useState({colorA:'#fff7e8',colorB:'#e37815'});
    React.useEffect(() => {
        const timer=setInterval(()=>{
            console.log(1);
            setlight(({colorA,colorB})=>{return {colorA:colorB,colorB:colorA}});
        },500);
        return () => {
            clearInterval(timer);
        }
    }, [lightcolors])
    const li4A=()=>{
        const res=[];
        for(let i=0;i<4;i++){
            res.push(<i style={{background:lightcolors.colorA}}></i>);
        }
        return res;
    }
    return (
        <div>
            {/* 外层闪烁 */}
            <ul className="wheel-light">
                
                <li><Li4 style={{background:lightcolors.colorA}} /></li>
                <li>{li4A()}</li>
                <li><i></i><i></i><i></i><i></i></li>
                <li>{li4A()}</li>
                <li><i></i><i></i><i></i><i></i></li>
                <li>{li4A()}</li>
            </ul>
        </div>
    )
}

function Li4(){
    return (
        <div>
            <i></i><i></i><i></i><i></i>
        </div>
    )
}
