import React,{useEffect,useState} from 'react'
import PubSub from 'pubsub-js'

export default function Showprize() {
    const [prizemsg,setprizemsg]=useState("");
    useEffect(() => {
        const token= PubSub.subscribe('Showprize',(msg,item)=>{
            console.log("设置中奖信息",item);
            setprizemsg(item);
        })
        return () => {
            PubSub.unsubscribe(token);
        }
    }, [prizemsg])
    return (
        // 遮罩层+弹出奖品
        <div>
            <div class="mask"></div>
            <div class="show-prize">
                <img src="./images/prize.png" alt="" class="prize-container"/>
                <div class="prize-info"></div>
                <img src="./images/close.png" alt="" class="close"/>
            </div>
        </div>
    )
}
