import React from 'react'
import './index.css'

export default function WheelList() {
    const prize_info=[];
    React.useEffect(() => {
        // 从后台获取奖品信息
        axios.post('https://qc1hz1.fn.thelarkcloud.com/prizeinfo')
        .then(function (res) {
            prize_info = res.data.response;
            console.log("奖品信息：",prize_info);
            prize_info.forEach((item,index)=>{
                // 是奖品则显示奖品名字
                if(item.isprize){
                    $(`#data${index+1}`).text(item.prize_name);
                }
            })
        });
        return () => {
            
        }
    }, [])

    return (
        // 中间奖品
        <ul id="wheel" className="wheel-list">
            <li>
                <i></i>
                <div className="prize">
                    <p id="data1">谢谢参与</p>
                    <img src="/images/prize_img1.png" alt=""/>
                </div>
            </li>
            <li>
                <i></i>
                <div className="prize">
                    <p id="data2">谢谢参与</p>
                    <img src="/images/prize_img2.png" alt=""/>
                </div>
            </li>
            <li>
                <i></i>
                <div className="prize">
                    <p id="data3">谢谢参与</p>
                    <img src="/images/prize_img3.png" alt=""/>
                </div>
            </li>
            <li>
                <i></i>
                <div className="prize">
                    <p id="data4">谢谢参与</p>
                    <img src="/images/prize_img4.png" alt=""/>
                </div>
            </li>
            <li>
                <i></i>
                <div className="prize">
                    <p id="data5">谢谢参与</p>
                    <img src="/images/prize_img5.png" alt=""/>
                </div>
            </li>
            <li>
                <i></i>
                <div className="prize">
                    <p id="data6">谢谢参与</p>
                    <img src="/images/prize_img6.png" alt=""/>
